mapper.select:
	select 
	
	,(SELECT
				count(1)
			FROM
				act_activity_store where activity_id=a.id)  as storeCount
	, (select count(id) from act_batch where activity_id=a.id) as orderCount
	from act_activity a
	
		
		
			and  ( a.name like '%${searchInfoContent}%' or a.code like '%${searchInfoContent}%' )
		
	
	
		
			order by a.code ${orderInfoDir}
		
		
			order by a.name ${orderInfoDir}
		
		
			order by a.start_time ${orderInfoDir}
		
		
			order by a.end_time ${orderInfoDir}
		
		
			order by a.createtime ${orderInfoDir}
		
	
	
		order by a.createtime desc
	
	
    	limit #{pageInfoStart,jdbcType=INTEGER},#{pageInfoLimit,jdbcType=INTEGER}

mapper.insert:
insert into act_activity
id, name, type, code, out_code, receipt_title, receipt_print, pos_success_msg, success_sms_msg, send_sms_type, notice_sms_msg, start_time, end_time, select_date, amount, max_type, max_number, integral_activity, integral, enterprise_id, enterprise_name, contract_id, contract_name, status, createtime, modifytime, creator, modifier, bins, exchange_type, channel
#{id}, #{name}, #{type}, #{code}, #{outCode}, #{receiptTitle}, #{receiptPrint}, #{posSuccessMsg}, #{successSmsMsg}, #{sendSmsType}, #{noticeSmsMsg}, #{startTime}, #{endTime}, #{selectDate}, #{amount}, #{maxType}, #{maxNumber}, #{integralActivity}, #{integral}, #{enterpriseId}, #{enterpriseName}, #{contractId}, #{contractName}, #{status}, #{createtime}, #{modifytime}, #{creator}, #{modifier}, #{bins}, #{exchangeType}, #{channel}

mapper.delete:
delete from act_activity

	
		and id = #{id}
	
	
		and name = #{name}
	
	
		and type = #{type}
	
	
		and code = #{code}
	
	
		and out_code = #{outCode}
	
	
		and receipt_title = #{receiptTitle}
	
	
		and receipt_print = #{receiptPrint}
	
	
		and pos_success_msg = #{posSuccessMsg}
	
	
		and success_sms_msg = #{successSmsMsg}
	
	
		and send_sms_type = #{sendSmsType}
	
	
		and notice_sms_msg = #{noticeSmsMsg}
	
	
		and start_time = #{startTime}
	
	
		and end_time = #{endTime}
	
	
		and select_date = #{selectDate}
	
	
		and amount = #{amount}
	
	
		and max_type = #{maxType}
	
	
		and max_number = #{maxNumber}
	
	
		and integral_activity = #{integralActivity}
	
	
		and integral = #{integral}
	
	
		and enterprise_id = #{enterpriseId}
	
	
		and contract_id = #{contractId}
	
	
		and status = #{status}
	
	
		and createtime = #{createtime}
	
	
		and modifytime = #{modifytime}
	
	
		and creator = #{creator}
	
	
		and modifier = #{modifier}
