mapper.select:

select 

,(select count(1) from t_contract c where c.enterprise_id=e.id) as contractCount
,(select count(1) from act_activity where enterprise_id=e.id) as activityCount
from t_enterprise e

	
		and create_time>#{startTime,jdbcType=TIMESTAMP}
	
	
		and create_time<#{endTime,jdbcType=TIMESTAMP}
	
	
		and (name like #{key} 
		or code like #{key}
		or linkman like #{key}
		or linkphone like #{key}
		or linkman2 like #{key} 
		or linkphone2 like #{key}
		or linkaddress like #{key}
		)
	
		and e.status !=2


select 

from t_enterprise e
where name=#{name}
and e.status !=2

	and e.id !=#{id}

limit 1
