mapper.select:

select 

,(select count(1) from t_store where merchant_id=m.id) as storeCount
from t_merchant m

	m.status!=2
	
	and m.id in (select merchant_id from t_contract_merchant where contract_id=#{contractId})
	
	
		and(name like #{key} 
		or code like #{key}
		or linkman like #{key}
		or linkphone like #{key}
		or address like #{key}
		or descr like #{key})
